// filepath: c:\Develop\Facultate\AN_III\SEM_II\AI\Proiect AI\fisher-random-chess\frontend\src\components\Board.js
import React, { useState, useEffect } from "react";
import Chessboard from "chessboardjsx";
import axios from "axios";

const Board = () => {
  const [fen, setFen] = useState("start");

  useEffect(() => {
    // Fetch the initial random setup
    axios.get("http://127.0.0.1:5000/api/setup").then((response) => {
      setFen(response.data);
    });
  }, []);

  const onDrop = ({ sourceSquare, targetSquare }) => {
    axios
      .post("http://127.0.0.1:5000/api/move", {
        from: sourceSquare,
        to: targetSquare,
        fen,
      })
      .then((response) => {
        if (response.data.status === "success") {
          setFen(response.data.fen);
        } else {
          alert(response.data.message);
        }
      })
      .catch(() => {
        alert("Invalid move!");
      });
  };

  return (
    <div>
      <h1>Fisher Random Chess</h1>
      <Chessboard position={fen} onDrop={onDrop} />
    </div>
  );
};

export default Board;
