import React, { useState, useEffect } from "react";
import Chessboard from "chessboardjsx";
import axios from "axios";
import MoveHistory from "./MoveHistory";
import Score from "./Score";

const Board = () => {
  const [fen, setFen] = useState("start");
  const [highlightedSquares, setHighlightedSquares] = useState({});
  const [moves, setMoves] = useState([]);
  const [capturedByWhite, setCapturedByWhite] = useState([]); // Piesele capturate de alb
  const [capturedByBlack, setCapturedByBlack] = useState([]); // Piesele capturate de negru

  useEffect(() => {
    // Fetch the initial random setup
    axios
      .get("http://127.0.0.1:5000/api/setup")
      .then((response) => {
        setFen(response.data);
      })
      .catch((error) => {
        console.error("Error fetching setup:", error);
      });
  }, []);

  const onDrop = ({ sourceSquare, targetSquare }) => {
    axios
      .post("http://127.0.0.1:5000/api/move", {
        from: sourceSquare,
        to: targetSquare,
        fen: fen,
      })
      .then((response) => {
        if (response.data.status === "success") {
          setFen(response.data.fen); // Update the board with the new FEN
          setMoves([...moves, `${sourceSquare}-${targetSquare}`]); // Update move history
          if (response.data.captured) {
            // Verificăm cine a capturat piesa
            if (response.data.turn === "w") {
              setCapturedByBlack((prev) => [...prev, response.data.captured]); // Negru a capturat
            } else {
              setCapturedByWhite((prev) => [...prev, response.data.captured]); // Alb a capturat
            }
          }
        } else {
          setHighlightedSquares({
            [targetSquare]: { backgroundColor: "rgba(255, 0, 0, 0.4)" }, // Red for invalid move
          });
          setTimeout(() => setHighlightedSquares({}), 1000); // Clear after 1 second
          alert(response.data.message || "Invalid move.");
        }
      })
      .catch((error) => {
        console.error("Error making move:", error);
        alert("Invalid move or server error.");
      });
  };

  const onMouseOverSquare = (square) => {
    axios
      .post("http://127.0.0.1:5000/api/legal_moves", { fen, square })
      .then((response) => {
        const legalMoves = response.data.moves;
        const highlights = {};
        legalMoves.forEach((move) => {
          highlights[move] = { backgroundColor: "rgba(0, 255, 0, 0.4)" }; // Green for valid moves
        });
        setHighlightedSquares(highlights);
      })
      .catch((error) => {
        console.error("Error fetching legal moves:", error);
      });
  };

  const onMouseOutSquare = () => {
    setHighlightedSquares({});
  };

  return (
    <div>
      <h2>Fisher Random Chess</h2>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        {/* Piesele capturate de alb */}
        <div>
          <h3>Captured by White:</h3>
          <div style={{ display: "flex", gap: "5px" }}>
            {capturedByWhite.map((piece, index) => (
              <span key={index}>{piece}</span>
            ))}
          </div>
        </div>

        {/* Tabla de șah */}
        <Chessboard
          position={fen}
          onDrop={onDrop}
          onMouseOverSquare={onMouseOverSquare}
          onMouseOutSquare={onMouseOutSquare}
          squareStyles={highlightedSquares}
          draggable={true}
        />

        {/* Piesele capturate de negru */}
        <div>
          <h3>Captured by Black:</h3>
          <div style={{ display: "flex", gap: "5px" }}>
            {capturedByBlack.map((piece, index) => (
              <span key={index}>{piece}</span>
            ))}
          </div>
        </div>
      </div>
      <MoveHistory moves={moves} />
      <Score captured={[...capturedByWhite, ...capturedByBlack]} />
    </div>
  );
};

export default Board;
